.stack-border-blue {
    position: relative;
    padding: 20px;
    margin: 20px 0;
}

.stack-border-blue::before,
.stack-border-blue::after {
    content: "";
    position: absolute;
    width: 20px;
    height: 20px;
    border: 2px solid #0038ff; /* Cor azul como na imagem */
}

/* Canto superior esquerdo */
.stack-border-blue::before {
    top: 0;
    left: 0;
    border-right: none;
    border-bottom: none;
}

/* Canto inferior direito */
.stack-border-blue::after {
    bottom: 0;
    right: 0;
    border-left: none;
    border-top: none;
}






