/* FAQ Styling for WordPress Details Element */
details {
    width: 100%;

    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    margin-bottom: 30px; /* Aumentei o espaço entre as perguntas */
padding: 2vh 0;
}

details:first-child {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
padding-top: 4vh;
}



details > summary {
    list-style: none;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 15px 0;
    cursor: pointer;
    color: #ffffff;
    font-size: 35px;
    font-weight: 500;
    transition: all 0.3s ease;
}

/* Remove default browser arrow */
details > summary::-webkit-details-marker,
details > summary::marker {
    display: none;
}

/* Custom arrow styling */
details > summary::after {
    content: '↗';
    background-color: transparent;
    color: #ffffff;
    padding: 8px 17px;
    border-radius: 40px; /* Ajustei o border-radius */
    border: 1px solid white; /* Adicionei borda branca */
    transition: all 0.3s ease;
}

/* Styling when details are open */
details[open] > summary {
    color: #FA962C;
}

details[open] > summary::after {
    background-color: #1E90FF;
    color: #ffffff;
    border-radius: 40px; /* Mantive o mesmo border-radius */
    transform: rotate(80deg);
    border: 1px solid white; /* Mantive a borda branca */
}

details > summary:hover {
    color: #FFC87C;
}

details > div {
    padding: 15px 0;
    color: #ffffff;
    opacity: 0.8;
    line-height: 1.6;
    font-size: 14px;
}

/* Optional: Background settings for dark theme */
details {
    background-color: #000000;
}

